mapper.select:
        select a.*
        from auth_resource a
        where a.type = 2 and not exists (
          select 1 from auth_role_resource b
		  where a.id = b.resource_id and b.role_id = #{roleId,jdbcType=INTEGER}
		)

        select a.*
        from auth_resource a
        where a.type = 1 and not exists (
          select 1 from auth_role_resource b
		  where a.id = b.resource_id and b.role_id = #{roleId,jdbcType=INTEGER}
		)
